import "typedoc";
declare module "typedoc" {
    interface Reflection {
        /**
         * Return a child by its name and type.
         *
         * @param {string | string[]} arg The name hierarchy of the child to look for. Can have `:kind` appended to the names to filter duplicate names based on their kind.
         * @returns {Reflection | undefined} The found child or undefined.
         */
        getChildByNameAndType(arg: string | string[]): Reflection | undefined;
    }
}
